mapper.select:
SELECT t.*, r.procInstId, r.approvalStatus FROM ( SELECT g.*, ( SELECT org.NAME_ FROM uc_org org WHERE ID_ = g.areaId ) AS areaName, ( SELECT org.NAME_ FROM uc_org org WHERE ID_ = g.projectId ) AS projectName, ( SELECT org.NAME_ FROM uc_org org WHERE ID_ = g.cityId ) AS cityName, ( SELECT org.NAME_ FROM uc_org org WHERE ID_ = g.stagingId ) AS stagingName, k.fullName AS housekeeperName FROM ( SELECT * from ( select t.id AS id, t.grid_range AS gridRange, s.service_grid_name AS gridName, t.grid_type AS gridType, s.service_grid_code AS gridCode, case s.enabled_flag when 1 then t.grid_remark else NULL end AS gridRemark, t.area_id AS areaId, t.project_id AS projectId, t.city_id AS cityId, s.staging_id AS stagingId, t.staging_id AS divideId, case s.enabled_flag when 1 then t.housekeeper_id else NULL end AS housekeeperId, t.format_attribute AS formatAttribute, t.second_format_attribute AS secondFormatAttribute, t.third_format_attribute AS thirdFormatAttribute, concat('', s.enabled_flag, '') AS enabledFlag, t.is_deleted AS isDeleted, t.creation_date AS creationDate, s.id AS serviceGridId FROM stage_servicegird_ref s LEFT JOIN grid_basic_info t ON t.staging_id = s.staging_id and t.grid_type='service_center_grid' where s.staging_id =#{massifId} and s.is_deleted = 0 and t.is_deleted = 0 UNION select t.id AS id, t.grid_range AS gridRange, t.grid_name AS gridName, t.grid_type AS gridType, t.grid_code AS gridCode, t.grid_remark AS gridRemark, t.area_id AS areaId, t.project_id AS projectId, t.city_id AS cityId, t.staging_id AS stagingId, t.staging_id AS divideId, t.housekeeper_id AS housekeeperId, t.format_attribute AS formatAttribute, t.second_format_attribute AS secondFormatAttribute, t.third_format_attribute AS thirdFormatAttribute, concat('', t.enabled_flag, '') AS enabledFlag, t.is_deleted AS isDeleted, t.creation_date AS creationDate, null AS serviceGridId from grid_basic_info t where t.staging_id=#{massifId} AND t.grid_type!='service_center_grid' AND t.is_deleted = 0 ) as a ) g LEFT JOIN ( SELECT u.ID_ AS houseKeeperId, u.FULLNAME_ AS fullName, u.MOBILE_ AS mobile, u.ACCOUNT_ AS account FROM uc_user u WHERE u.IS_DELE_ = 0 ) AS k ON g.housekeeperId = k.houseKeeperId ) AS t LEFT JOIN ( SELECT gr.grid_id, gr.approval_status AS approvalStatus, gr.proc_inst_Id AS procInstId FROM grid_approval_record gr WHERE gr.create_date = ( SELECT max(create_date) FROM grid_approval_record WHERE grid_id = gr.grid_id ) ) r ON r.grid_id = t.id
${whereSql}
GROUP BY t.id
ORDER BY ${orderBySql}

SELECT t.*, r.procInstId, r.approvalStatus FROM ( SELECT g.*, ( SELECT org.NAME_ FROM uc_org org WHERE ID_ = g.areaId ) AS areaName, ( SELECT org.NAME_ FROM uc_org org WHERE ID_ = g.projectId ) AS projectName, ( SELECT org.NAME_ FROM uc_org org WHERE ID_ = g.cityId ) AS cityName, ( SELECT org.NAME_ FROM uc_org org WHERE ID_ = g.stagingId ) AS stagingName, k.fullName AS housekeeperName FROM ( SELECT * from ( select t.id AS id, t.grid_range AS gridRange, s.service_grid_name AS gridName, t.grid_type AS gridType, s.service_grid_code AS gridCode, t.grid_remark AS gridRemark, null AS areaId, null AS projectId, null AS cityId, s.staging_id AS stagingId, t.staging_id AS divideId, t.housekeeper_id AS housekeeperId, t.format_attribute AS formatAttribute, t.second_format_attribute AS secondFormatAttribute, t.third_format_attribute AS thirdFormatAttribute, concat('', s.enabled_flag, '') AS enabledFlag, t.is_deleted AS isDeleted, t.creation_date AS creationDate FROM stage_servicegird_ref s LEFT JOIN grid_basic_info t ON t.staging_id = s.staging_id and t.grid_type='service_center_grid' where s.staging_id =#{massifId} and s.is_deleted = 0 AND s.enabled_flag = 1 and t.is_deleted = 0 AND t.enabled_flag = 1 UNION select t.id AS id, t.grid_range AS gridRange, t.grid_name AS gridName, t.grid_type AS gridType, t.grid_code AS gridCode, t.grid_remark AS gridRemark, t.area_id AS areaId, t.project_id AS projectId, t.city_id AS cityId, t.staging_id AS stagingId, t.staging_id AS divideId, t.housekeeper_id AS housekeeperId, t.format_attribute AS formatAttribute, t.second_format_attribute AS secondFormatAttribute, t.third_format_attribute AS thirdFormatAttribute, concat('', t.enabled_flag, '') AS enabledFlag, t.is_deleted AS isDeleted, t.creation_date AS creationDate from grid_basic_info t where t.staging_id=#{massifId} AND t.grid_type !='service_center_grid' AND t.is_deleted = 0 AND t.enabled_flag = 1 ) as a ) g LEFT JOIN ( SELECT u.ID_ AS houseKeeperId, u.FULLNAME_ AS fullName, u.MOBILE_ AS mobile, u.ACCOUNT_ AS account FROM uc_user u WHERE u.IS_DELE_ = 0 and u.STATUS_ = 1 ) as k on g.housekeeperId = k.houseKeeperId ) as t LEFT JOIN ( SELECT gr.grid_id, gr.approval_status AS approvalStatus, gr.proc_inst_Id AS procInstId FROM grid_approval_record gr WHERE gr.create_date = ( SELECT max( create_date ) FROM grid_approval_record WHERE grid_id = gr.grid_id ) ) r ON r.grid_id = t.id
${whereSql}
and (approvalStatus is null or approvalStatus =2)
GROUP BY t.id
ORDER BY ${orderBySql}

mapper.update:
update grid_basic_info
set is_deleted = 1, grid_range = null, update_times = update_times + 1, updated_by = #{updatedBy}
where id in

    #{id}
