mapper.select:
SELECT a.id, a.project_id, a.area_id, a.massif_id, a.staging_id, a.city_id, a.grid_code, a.grid_name, a.grid_type, a.grid_range, a.grid_remark, a.update_times, a.format_attribute, a.housekeeper_id, a.created_by, a.creation_date, a.enabled_flag FROM grid_basic_info a where a.is_deleted = 0
and a.area_id = #{areaId}
and a.project_id = #{projectId}
and a.massif_id = #{massifId}
and a.staging_id = #{stagingId}
and INSTR(a.grid_code,#{gridCode}) > 0
and INSTR(a.grid_name,#{gridName})>0
and a.grid_type = gridType
and a.format_attribute = formatAttribute
order by a.creation_date desc

select * from ( select g.id as id, g.grid_range as gridRange, g.grid_name as gridName, g.grid_type as gridType, g.grid_code as gridCode, g.grid_remark as gridRemark, g.area_id as areaId, g.project_id as projectId, g.city_id as cityId, g.massif_id as massifId, g.staging_id as stagingId, g.staging_id as divideId, g.housekeeper_id as housekeeperId, g.format_attribute as formatAttribute, g.enabled_flag as enabledFlag, (select org.NAME_ from uc_org org where ID_ = g.area_id) as areaName, (select org.NAME_ from uc_org org where ID_ = g.project_id) as projectName, (select org.NAME_ from uc_org org where ID_ = g.city_id) as cityName, (select org.NAME_ from uc_org org where ID_ = g.massif_id) as massifName, (select org.NAME_ from uc_org org where ID_ = g.staging_id) as stagingName, k.fullName as housekeeperName from grid_basic_info g LEFT JOIN (SELECT u.ID_ AS houseKeeperId, uou.ID_ AS id, u.FULLNAME_ AS fullName, u.MOBILE_ AS mobile, u.ACCOUNT_ AS account, post.ID_ AS `postId`, post.POS_NAME_ AS posName, org.NAME_ AS divideName, org.ID_ AS divideId FROM uc_user u JOIN uc_org_user uou ON u.ID_ = uou.USER_ID_ JOIN uc_org org ON uou.ORG_ID_ = org.ID_ JOIN uc_org_post post ON uou.POS_ID_ = post.ID_ JOIN uc_org_job uoj ON post.JOB_ID_ = uoj.ID_ JOIN uc_demension dem ON org.DEM_ID_ = dem.ID_ WHERE uoj.CODE_ = 'guanjia' AND dem.CODE_ = 'smsf' AND org.LEVEL_ = 5) as k on g.staging_id = k.divideId and g.housekeeper_id = k.houseKeeperId) as t
${whereSql}
ORDER BY ${orderBySql}
