mapper.select:
SELECT a.id, a.project_id, a.area_id, a.massif_id, a.staging_id, a.city_id, a.grid_code, a.grid_name, a.grid_type, a.grid_range, a.grid_remark, a.update_times, a.format_attribute, a.housekeeper_id, a.created_by, a.creation_date, a.enabled_flag FROM grid_basic_info a where a.is_deleted = 0
and a.area_id = #{areaId}
and a.project_id = #{projectId}
and a.massif_id = #{massifId}
and a.staging_id = #{stagingId}
and INSTR(a.grid_code,#{gridCode}) > 0
and INSTR(a.grid_name,#{gridName})>0
and a.grid_type = gridType
and a.format_attribute = formatAttribute
order by a.creation_date desc

SELECT * from ( select g.id as gridId, g.grid_code AS gridCode, g.grid_name AS gridName, g.grid_type AS gridType, g.grid_remark AS gridRemark, g.format_attribute AS formatAttribute, u.ID_ AS houseKeeperId, u.FULLNAME_ AS fullName, u.MOBILE_ AS mobile, u.ACCOUNT_ AS account, post.ID_ AS `postId`, post.POS_NAME_ AS posName, org_lv5.NAME_ AS areaName, org_lv4.NAME_ AS cityNamme, org_lv3.NAME_ AS projectName, org_lv2.NAME_ AS plotName, org.NAME_ AS divideName, org_lv5.ID_ AS areaId, org_lv4.ID_ AS cityId, org_lv3.ID_ AS projectId, org_lv2.ID_ AS plotId, org.ID_ AS divideId from grid_basic_info g join uc_user u on g.housekeeper_id = u.ID_ join uc_org_user uou on u.ID_ = uou.USER_ID_ join uc_org org on uou.ORG_ID_ = org.ID_ join uc_org org_lv2 on org.PARENT_ID_ = org_lv2.ID_ join uc_org org_lv3 on org_lv2.PARENT_ID_ = org_lv3.ID_ join uc_org org_lv4 on org_lv3.PARENT_ID_ = org_lv4.ID_ join uc_org org_lv5 on org_lv4.PARENT_ID_ = org_lv5.ID_ join uc_org_post post on uou.POS_ID_ = post.ID_ join uc_org_job uoj on post.JOB_ID_ = uoj.ID_ join uc_demension dem on org.DEM_ID_ = dem.ID_ and org_lv2.DEM_ID_ = dem.ID_ and org_lv3.DEM_ID_ = dem.ID_
and org_lv4.DEM_ID_ = dem.ID_ and org_lv5.DEM_ID_ = dem.ID_ where uoj.CODE_ = 'guanjia' and dem.CODE_ = 'smsf' and org.LEVEL_ = 5) a
${whereSql}
ORDER BY ${orderBySql}
