SELECT DISTINCT u.FULLNAME_ fullName,u.USER_NUMBER_ userNumber,rsum.january,rsum.february,rsum.march,rsum.april,rsum.may,rsum.june,rsum.july,rsum.angust,rsum.september,rsum.october,rsum.november,rsum.december,u.ID_ userId,february+january+march+april+may+june+july+angust+september+october+november+december totalNum FROM (SELECT SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=01 THEN r.gcoin_val ELSE 0 END ) january, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=02 THEN r.gcoin_val ELSE 0 END ) february, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=03 THEN r.gcoin_val ELSE 0 END ) march, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=04 THEN r.gcoin_val ELSE 0 END ) april, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=05 THEN r.gcoin_val ELSE 0 END ) may, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=06 THEN r.gcoin_val ELSE 0 END ) june, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=07 THEN r.gcoin_val ELSE 0 END ) july, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=08 THEN r.gcoin_val ELSE 0 END ) angust, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=09 THEN r.gcoin_val ELSE 0 END ) september, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=10 THEN r.gcoin_val ELSE 0 END ) october, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=11 THEN r.gcoin_val ELSE 0 END ) november, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=12 THEN r.gcoin_val ELSE 0 END ) december, r.uc_user_id userId FROM uc.rsun_jb_hi_reward r
DATE_FORMAT(r.jb_originnal_time ,'%Y')=#{yearForSearch}
GROUP BY userId) rsum RIGHT JOIN uc.uc_user u ON rsum.userId=u.ID_ LEFT JOIN uc.uc_org_user c ON u.ID_=c.USER_ID_
1=1
AND u.FULLNAME_ like concat('%',#{FULLNAME_},'%')
AND c.ORG_ID_ in ${projectForSearch}
AND c.POS_ID_ in ${jobForSearch}
AND c.IS_DELE_=0
ORDER BY ${orderBySql}

SELECT u.FULLNAME_ fullName,u.USER_NUMBER_ userNumber,rsum.january,rsum.february,rsum.march,rsum.april,rsum.may,rsum.june,rsum.july,rsum.angust,rsum.september,rsum.october,rsum.november,rsum.december,u.ID_ userId,february+january+march+april+may+june+july+angust+september+october+november+december totalNum FROM (SELECT SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=01 THEN r.gcoin_val ELSE 0 END ) january, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=02 THEN r.gcoin_val ELSE 0 END ) february, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=03 THEN r.gcoin_val ELSE 0 END ) march, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=04 THEN r.gcoin_val ELSE 0 END ) april, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=05 THEN r.gcoin_val ELSE 0 END ) may, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=06 THEN r.gcoin_val ELSE 0 END ) june, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=07 THEN r.gcoin_val ELSE 0 END ) july, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=08 THEN r.gcoin_val ELSE 0 END ) angust, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=09 THEN r.gcoin_val ELSE 0 END ) september, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=10 THEN r.gcoin_val ELSE 0 END ) october, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=11 THEN r.gcoin_val ELSE 0 END ) november, SUM(CASE WHEN DATE_FORMAT(r.jb_originnal_time ,'%m')=12 THEN r.gcoin_val ELSE 0 END ) december, r.uc_user_id userId FROM uc.rsun_jb_hi_reward r GROUP BY userId) rsum RIGHT JOIN uc.uc_user u ON rsum.userId=u.ID_ LEFT JOIN uc.uc_org_user c ON u.ID_=c.USER_ID_
u.ID_ in
#{userId}
