mapper.select:
select o.NAME_ as orgName, t.id as id, t.order_ as 'order', t.type as type, t.org_code as orgCode, t.overall_satisfaction as overallSatisfaction, t.storming as storming, t.stationary_phase as stationaryPhase, t.old_proprietor as oldProprietor, t.order_service_unit as orderServiceUnit, t.esu_cleaning as esuCleaning, t.esu_green as esuGreen, t.engineering_service_unit as engineeringServiceUnit, t.create_time as createTime, t.create_by as createBy, t.update_by as updateBy, t.update_time as updateTime, t.remark as remark, t.effective_time as effectiveTime from uc_org o LEFT JOIN ( SELECT * FROM t_satisfaction WHERE DATE_FORMAT(effective_time, '%Y-%m') = #{time} AND org_code IN
#{item.code}
) t on o.CODE_=org_code where o.IS_DELE_!=1

select t.id as id, t.order_ as 'order', t.type as type, t.org_name as orgName, t.org_code as orgCode, t.overall_satisfaction as overallSatisfaction, t.storming as storming, t.stationary_phase as stationaryPhase, t.old_proprietor as oldProprietor, t.order_service_unit as orderServiceUnit, t.esu_cleaning as esuCleaning, t.esu_green as esuGreen, t.engineering_service_unit as engineeringServiceUnit, t.create_time as createTime, t.create_by as createBy, t.update_by as updateBy, t.update_time as updateTime, t.remark as remark, t.effective_time as effectiveTime from t_satisfaction t where 1=1
and t.org_Name in ( #{value.name1},#{value.name2},#{value.name3},#{value.name4} )
and t.effective_time >= #{value.beginDate} and t.effective_time <= #{value.endDate}
