mapper.select:
        select staging_id,service_grid_id
        from stage_servicegird_ref
        where staging_id=#{stagingId}
        and is_deleted = 0
		AND enabled_flag = 1
		limit 1;

select service_grid_id
from stage_servicegird_ref
where staging_id=#{stagingId}
and is_deleted = 0
limit 1;
